import React, {Component, Fragment} from 'react';
import {Switch, Route, withRouter, Redirect} from 'react-router-dom';
import PrivateRoute from '../components/Nav/PrivateRoute';
import {connect} from "react-redux";
import {db_get_customer} from '../store/actions/customers';
import {db_get_worker} from '../store/actions/workers';
import {db_get_admin} from '../store/actions/admin';
import {setFooter} from '../store/actions/footer';

import Dashboard from './Customer/Dashboard';
import SignIn from './Customer/SignIn';
import Book from './Customer/Book';
import Manage from './Customer/Manage';
import Account from './Customer/Account';

import WorkerDashboard from './Worker/WorkerDashboard';
import WorkerSignIn from './Worker/WorkerSignIn';
import FindJob from './Worker/FindJob';
import WManage from './Worker/Manage';
import WAccount from './Worker/Account';

import AdminDashboard from './Admin/AdminDashboard';
import AdminSignIn from './Admin/AdminSignIn';

import NoRoute from '../components/Graphics/NoRoute';

class Main extends Component {
	constructor(props) {
		super(props);
		this.state = {
			loaded: false
		};
	}

	async componentDidMount() {
		if(this.props.user.db == "customers") {
			await this.props.db_get_customer(this.props.user);
			this.props.setFooter("customers");
		}
		if(this.props.user.db == "workers") {
			await this.props.db_get_worker(this.props.user);
			this.props.setFooter("workers");
		}
		if(this.props.user.db == "admin") {
			await this.props.db_get_admin(this.props.user);
			this.props.setFooter("admin");
		}
		this.setState({
			...this.state,
			loaded: true
		});
	}

  onChange = e => {
    this.setState({ 
    	[e.target.id]: e.target.value 
    });
  };

	render() {
		return (
			<Fragment>
				{this.state.loaded && (<Switch>
	        <Route exact path='/app' render={props => <SignIn signin/>} />
	        <Route exact path='/app/signin' render={props => <SignIn signin/>} />
	        <Route exact path='/app/signup' render={props => <SignIn signin={false}/>} />
	        <PrivateRoute exact path='/app/dashboard' component={Dashboard} portal="customers"/>
	        <PrivateRoute exact path='/app/dashboard/book' component={Book} portal="customers"/>
	        <PrivateRoute exact path='/app/dashboard/manage' component={Manage} portal="customers"/>
	        <PrivateRoute exact path='/app/dashboard/manage/:section' component={Manage} portal="customers"/>
	        <PrivateRoute exact path='/app/dashboard/manage/:section/:id' component={Manage} portal="customers"/>
	        <PrivateRoute exact path='/app/dashboard/account' component={Account} portal="customers"/>
	        <PrivateRoute exact path='/app/dashboard/account/:section' component={Account} portal="customers"/>
	        <Route exact path='/worker' render={props => <WorkerSignIn signin/>} />
	        <Route exact path='/worker/signin' render={props => <WorkerSignIn signin/>} />
	        <Route exact path='/worker/signup' render={props => <WorkerSignIn signin={false}/>} />
	        <PrivateRoute exact path='/worker/dashboard' component={WorkerDashboard} portal="workers"/>
	        <PrivateRoute exact path='/worker/dashboard/find' component={FindJob} portal="workers"/>
	        <PrivateRoute exact path='/worker/dashboard/manage' component={WManage} portal="workers"/>
	        <PrivateRoute exact path='/worker/dashboard/manage/:section' component={WManage} portal="workers"/>
	        <PrivateRoute exact path='/worker/dashboard/manage/:section/:id' component={WManage} portal="workers"/>
	        <PrivateRoute exact path='/worker/dashboard/account' component={WAccount} portal="workers"/>
	        <PrivateRoute exact path='/worker/dashboard/account/:section' component={WAccount} portal="workers"/>
	        <Route exact path='/admin' render={props => <AdminSignIn signin/>} />
	        <PrivateRoute exact path='/admin/dashboard' component={AdminDashboard} portal="admin"/>
	        <PrivateRoute exact path='/admin/dashboard/:table' component={AdminDashboard} portal="admin"/>
	        <Route path='/app' render={props => <Redirect to="/app"/>} />
	        <Route path='/worker' render={props => <Redirect to="/worker"/>} />
	        <Route path='/admin' render={props => <Redirect to="/admin"/>} />
	        <Route path='*' render={props => <NoRoute />} />
	      </Switch>)}
      </Fragment>
		)
	}
}

const mapStateToProps = state => ({
  user: state.auth.user,
  allowed: state.auth.isAuthenticated
});

export default connect(mapStateToProps,{db_get_customer, db_get_worker, db_get_admin, setFooter})(withRouter(Main));
