import React, {Component, Fragment} from 'react';
import {Link, withRouter} from 'react-router-dom';
import {connect} from "react-redux";
import moment from 'moment';
import empty from 'is-empty';
import {jobtypes} from '../../utils/options';
import {db_add_job, db_get_all_paid_jobs} from '../../store/actions/jobs';
import {db_get_workers_by_loc} from '../../store/actions/workers';
import validator from 'validator';
import {states} from '../../utils/options';
import checkTrue from '../../utils/checkTrue';
import {WORKER_RATE} from '../../utils/constants';

import flowers from '../../images/flowers.jpeg';
import powered from '../../images/stripepowered.png';

import Navbar from '../../components/Nav/Navbar';
import DatePicker from '../../components/Forms/DatePicker';
import TimePick from '../../components/Forms/TimePick';
import Dropdown from '../../components/Forms/Dropdown';
import AddressBlock from '../../components/Forms/AddressBlock';
import CostEstimate from '../../components/Graphics/CostEstimate';
import Loading from '../../components/Graphics/Loading';
import PaymentInfo from '../../components/Forms/PaymentInfo';
import ErrorBanner from '../../components/Graphics/ErrorBanner';

class Book extends Component {
	constructor(props) {
		super(props);
		this.state = {
			data: {
				customer_id: this.props.auth.user.id,
				date: null,
				time: 0,
				phone: this.props.auth.user.phone,
				address: this.props.auth.user.address,
				city: this.props.auth.user.city,
				state: this.props.auth.user.state,
				zip: this.props.auth.user.zip,
				type: '',
				description: '',
				hours: '',
				workers: 1,
				same_day: false
			},
			validation: {
				date: true,
				time: true,
				phone: true,
				state: true,
				same_day_time: true
			},
			empties: [],
			small: false,
			loaded: false,
			selectedType: null,
			needsPayment: {},
			noworkers: false,
			needCard: false,
			cardLoading: false,
			success: "",
			error: ""
		};
	}

	resize() {
    let currentHideNav = (window.innerWidth <= 760);
    if (currentHideNav !== this.state.small) {
        if(currentHideNav) this.setState({small: true});
        if(!currentHideNav) {
		    	this.setState({small: false});
		    }
    }
	}

	async componentDidMount() {
		window.addEventListener("resize", this.resize.bind(this));
    this.resize();
    const worker_count = await db_get_workers_by_loc({lat: this.props.auth.user.lat, lon: this.props.auth.user.lon});
    const needsPay = await db_get_all_paid_jobs(this.props.auth.user.id);
    this.setState({...this.state, loaded: true, needsPayment: needsPay.complete ? needsPay.data : {}, noworkers: empty(worker_count.count)});
	}

	componentWillReceiveProps(nextProps) {
  	this.setState({
  		...this.state,
  		data: {
  			...this.state.data,
  			customer_id: nextProps.auth.user.id,
	  		phone: nextProps.auth.user.phone,
				address: nextProps.auth.user.address,
				city: nextProps.auth.user.city,
				state: nextProps.auth.user.state,
				zip: nextProps.auth.user.zip
  		}
  	})
  }

  onChange = e => {
    this.setState({
    	data: {
    		...this.state.data,
    		[e.target.id]: e.target.value 
    	} 
    });
  };

  onNumChange = e => {
  	let num = e.target.value > 4 ? 4 : e.target.value < 1 ? '' : Math.floor(e.target.value);
  	this.setState({
  		...this.state,
  		data: {
  			...this.state.data,
  			workers: num
  		}
  	})
  }

  handleDateChange = day => {
  	const {data} = this.state;
  	const now = (moment().hour()*60) + moment().minute();
  	const ok = empty(data.time) ? true : (moment(moment(day).format("YYYY-MM-DD")).isoWeekday() < 6) ? (data.time >= 900 && data.time <= 1200) : (data.time >= 480 && data.time <= 1200);
  	const timeok = empty(data.time) ? true : moment(day).isSame(moment(new Date()), 'day') ? (data.time > now) : true;
  	if(moment(day).isSameOrAfter(moment(new Date()).startOf('day'))) {
  		this.setState({
	  		...this.state,
	  		data: {
	  			...this.state.data,
	  			date: moment(day).format("YYYY-MM-DD"),
	  			same_day: moment(day).isSame(moment(new Date()), 'day')
	  		},
	  		validation: {
	  			...this.state.validation,
	  			date: true,
	  			time: ok,
	  			same_day_time: timeok
	  		}
	  	});
  	} else {
  		this.setState({
	  		...this.state,
	  		data: {
	  			...this.state.data,
	  			date: '',
	  		},
	  		validation: {
	  			...this.state.validation,
	  			date: false
	  		}
	  	});
  	}

  }

  handleTimeChange = time => {
  	let t = empty(time) ? null : moment(time).hour()*60 + moment(time).minute();
  	const now = (moment().hour()*60) + moment().minute();
  	const ok = empty(t) ? true : (moment(this.state.data.date).isoWeekday() < 6) ? (t >= 900 && t <= 1200) : (t >= 480 && t <= 1200);
  	const sameday = empty(t) ? true : this.state.data.same_day ? (t > now) : true;
  	this.setState({
  		...this.state,
  		data: {
  			...this.state.data,
  			time: t
  		},
  		validation: {
  			...this.state.validation,
  			time: ok,
  			same_day_time: sameday
  		}
  	});
  }

  onPhoneChange = e => {
  	const ok = validator.isMobilePhone(e.target.value, 'en-US');
		this.setState({
			...this.state,
			data: {
				...this.state.data,
				phone: e.target.value
			},
			validation: {
				...this.state.validation,
				phone: ok
			}
		});
  }

  onStateChange = e => {
  	const ok = states.includes(e.target.value.toUpperCase());
		this.setState({
			...this.state,
			data: {
				...this.state.data,
				state: e.target.value
			},
			validation: {
				...this.state.validation,
				state: ok
			}
		});
  }

  handleJobChange = value => {
  	this.setState({
  		...this.state,
  		data: {
  			...this.state.data,
  			type: empty(value) ? null : value.value
  		},
  		selectedType: value
  	});
  }

  checkEmpties = () => {
  	let arr = [];
  	const {data} = this.state;
  	for (var key in data) {
	    if (empty(data[key])) {
	      arr.push(key);
	    }
		}
		if(empty(arr)) {
			return true;
		} else {
			this.setState({
				...this.state,
				empties: arr
			});
			return false;
		}
  }

  onSubmit = async e => {
  	e.preventDefault();
  	await this.setState({
  		...this.state,
  		empties: [],
  		loaded: false
  	});
  	const empties = await this.checkEmpties();
  	if(empties && checkTrue(this.state.validation)) {
  		const resp = await this.props.db_add_job(this.state.data);
  		resp.complete ? this.props.history.push('/app/dashboard/manage') : console.log(resp.message);
  	}
  	this.setState({
  		...this.state,
  		date: this.state.date,
  		type: this.state.type,
  		loaded: true
  	});
  }

	render() {
		const {data, validation, empties, selectedType, needsPayment, noworkers, needCard, cardLoading} = this.state;
		return (
			<Fragment>
				<Navbar active="book"/>
				{!this.state.loaded && (<Loading />)}
				{this.state.cardLoading && (<Loading />)}
				{!empty(this.state.success) && (<ErrorBanner style={{background: "green"}} error={this.state.success} onHide={() => this.setState({...this.state, success: ''})}/>)}
				{!empty(this.state.error) && (<ErrorBanner error={this.state.error} onHide={() => this.setState({...this.state, error: ''})}/>)}
				{this.state.loaded && (<Fragment>
					{!noworkers && empty(needsPayment) && !needCard && (<div className="sign-in-container no-back">
						<div className="book-box-img">
							<img className="book-header-img" src={flowers} alt=""/>
							<div className="book-title-div"><span className="book-title">BOOK A JOB</span></div>
							<div className="sign-up-box" style={{width: "100%", margin: "0", borderRadius: "0px", boxShadow: "none", paddingTop: "1rem"}}>
								<div className="input-horiz-container">
									<div className="input-side-container" style={{width: this.state.small ? "100%" : "38%", flexDirection: "column", justifyContent: "flex-start"}}>
										<span className="book-subtitle">Customer Info</span>
										<div className="input-side" style={{width: "100%"}}>
											<div className="input-group">
												<span className="field-label">Phone</span>
												{!validation.phone && (<span className="form-error">Enter a valid phone number.</span>)}
												<input type="text" className="text-field" id="phone" onChange={this.onPhoneChange} value={data.phone}/>
												{empties.includes("phone") && (<span className="form-error">You must enter a phone number.</span>)}
											</div>
											<AddressBlock onChange={this.onChange} onStateChange={this.onStateChange} state={this.state}/>
										</div>
									</div>
									{this.state.small && (<br/>)}
									<div className="input-side-container" style={{width: this.state.small ? "100%" : "58%"}}>
										<span className="book-subtitle">Job Info</span>
										<div className="input-side">
											<div className="input-group">
												<span className="field-label">Date</span>
												{!validation.date && (<span className="form-error">You cannot select a date in the past.</span>)}
												<DatePicker onChange={this.handleDateChange} value={empty(data.date) ? null : data.date}/>
												{empties.includes("date") && (<span className="form-error">You must enter a date.</span>)}
											</div>
											<div className="input-group">
												<span className="field-label">Time <span style={{fontSize: "0.6rem"}}>(M-F 3-8pm, Sat-Sun 8am-8pm)</span></span>
												{!validation.same_day_time && (<span className="form-error">You cannot book a job in the past.</span>)}
												{!validation.time && (<span className="form-error">Select a time compliant with (M-F 3-8pm, Sat-Sun 8am-8pm).</span>)}
												<TimePick onChange={this.handleTimeChange} value={empty(data.time) ? null : moment.utc().startOf('day').add(data.time, 'minutes')}/>
												{empties.includes("time") && (<span className="form-error">You must enter a time.</span>)}
											</div>
										</div>
										<div className="input-side">
											<div className="input-group">
												<span className="field-label">Estimated Hours</span>
												<input min="0" type="number" className="text-field" id="hours" onChange={this.onChange} value={data.hours}/>
												{empties.includes("hours") && (<span className="form-error">You must enter estimated hours.</span>)}
											</div>
											<div className="input-group">
												<span className="field-label">Workers (1-4)</span>
												<input pattern="[0-9]" type="number" step="1" min="1" max="4" className="text-field" id="workers" onChange={this.onNumChange} value={data.workers}/>
												{empties.includes("workers") && (<span className="form-error">You must enter the number of workers you need.</span>)}
											</div>
										</div>
										<div className="input-side horiz-flex">
											<div className="input-group">
												<span className="field-label">Job Type</span>
												<Dropdown options={jobtypes} onChange={this.handleJobChange} value={selectedType}/>
												{empties.includes("type") && (<span className="form-error">You must enter a job type.</span>)}
											</div>
											<div className="input-group" style={{fontSize: "0.7rem", color: "#3f6bb4"}}>
												NOTE: The following jobs are not offered: 
												Electrical Work, Plumbing, Painting, and Carpentry.
											</div>
										</div>
										<div className="input-group bottom-form">
											<span className="field-label" style={{marginBottom: "0"}}>Description</span>
											<span style={{fontSize: "0.8rem", marginBottom: "0.3rem"}}>Tell us about your job.</span>
											<textarea type="text" className="description-field" id="description" onChange={this.onChange} value={data.description}/>
											{empties.includes("description") && (<span className="form-error">You must enter a description.</span>)}
										</div>
									</div>
								</div>
								<div className="bottom-form" style={{fontWeight: "500", fontSize: "1.2rem", width: "100%", marginTop: "0.5rem"}}>
									<div className="customer-info-text estimate"><span ><strong>ESTIMATED COST:</strong></span> <CostEstimate job={data}/></div>
								</div>
								{data.same_day && (<div className="bottom-form" style={{fontSize: "1rem", width: "100%", marginTop: "0.25rem", color: "red"}}>
									<span>WARNING: </span>
									<span>Because you are booking a job to be completed today, it is less likely that your job will be fulfilled.</span>
								</div>)}
								<button className="sign-in-btn bottom-form" type="submit" onClick={this.props.auth.user.card ? this.onSubmit : () => this.setState({...this.state, needCard: true})} disabled={!this.state.loaded}>Confirm</button>
							</div>
						</div>
					</div>)}
					{needCard && (
						<div className="sign-in-container no-card" style={cardLoading ? {opacity: 0} : {}}>
							<div className="sign-up-box" style={{width: "50rem", borderRadius: "0px", marginTop: "0"}}>
								<span className="sign-in-title">Before you continue...</span>
								<div style={{textAlign: "left", fontSize: "1rem"}}>
									<span style={{fontSize: "1.1rem"}}>There needs to be a credit card on file in order to finish booking your job. Don't worry, you won’t be charged until the job is complete.</span>
									<br/><br/>
									<span style={{fontSize: "1.1rem"}}>Why do I need to add a Credit Card?: </span>
									<ul>
										<li><strong>Seamless Security:</strong> Allows us to verify your account</li>
										<li><strong>Simplified Payment:</strong> Pay and sign at the job site without having to use cash</li>
										<li><strong>Not Home? No Problem:</strong> Allows for an invoice to be sent if you aren't home</li>
									</ul>
								</div>
								<img className="stripe-powered-img" src={powered} alt="" style={{alignSelf: "flex-start"}}/>
								<PaymentInfo user={this.props.auth.user} onCancel={() => {this.setState({...this.state, loaded: true, cardLoading: false, needCard: false, success: "Your card has been successfully linked to your profile."})}} onLoading={() => {this.setState({...this.state, cardLoading: true})}} onError={(err) => {this.setState({...this.state, error: err, loaded: true})}}/>
							</div>
						</div>
					)}
					{!empty(needsPayment) && (
						<div className="sign-in-container no-card">
							<div className="sign-up-box" style={{width: "50rem", borderRadius: "0px", marginTop: "0"}}>
								<span className="sign-in-title">Before you continue...</span>
								<div style={{textAlign: "left", fontSize: "1rem"}}>
									<span style={{fontSize: "1.1rem"}}>You have an outstanding payment that you need to complete. Click the link below to pay for the job.</span>
								</div>
								<a className="book-now-btn" href={needsPayment.invoice_url} style={{marginTop: "1rem"}} target="_blank">PAY FOR JOB</a>
							</div>
						</div>
					)}
					{noworkers && (
						<div className="sign-in-container no-card">
							<div className="sign-up-box" style={{width: "50rem", borderRadius: "0px", marginTop: "0"}}>
								<span className="sign-in-title">No workers in the area :(</span>
								<div style={{textAlign: "left", fontSize: "1rem"}}>
									<span style={{fontSize: "1.1rem"}}>Unfortunately, there are no workers signed up in your immediate vicinity. Therefore, we cannot let you book a job because there will be no workers to accept it. We look forward to serving you soon!</span>
								</div>
							</div>
						</div>
					)}
				</Fragment>)}
			</Fragment>
		)
	}
}

const mapStateToProps = state => ({
  auth: state.auth
});

export default connect(mapStateToProps,{db_add_job})(withRouter(Book));
