import React, {Component, Fragment} from 'react';
import {Link, withRouter} from 'react-router-dom';
import {connect} from "react-redux";
import {logoutUser} from "../../store/actions/auth";
import {db_get_recent_job, db_get_all_paid_jobs} from "../../store/actions/jobs";
import getTime from '../../utils/getTime';
import {WORKER_RATE} from '../../utils/constants';
import moment from 'moment';
import empty from 'is-empty';

import bookimg from '../../images/bookimg.png';
import joelogo from '../../images/joelogowhite.png';

import Navbar from '../../components/Nav/Navbar';
import Calendar from '../../components/Graphics/Calendar';
import Loading from '../../components/Graphics/Loading';
import JobDetail from '../../components/Forms/JobDetail';

class Checkout extends Component {
	constructor(props) {
		super(props);
		this.state = {
			loaded: false
		};
	}

  async componentDidMount() {
  	this.setState({
  		loaded: true
  	});
  }

  onRefresh = async () => {
  	const data = await db_get_recent_job(this.props.user.id);
  	this.setState({
  		job: data.complete ? data.data : null
  	});
  }

  onHide = () => {
  	this.setState({
  		...this.state,
  		detail: false
  	});
  	document.body.style.overflow = "auto";
  }

  setDetail = e => {
  	e.preventDefault();
  	this.setState({
  		...this.state,
  		detail: true
  	});
  	document.body.style.overflow = "hidden";
  }

	render() {
		const {job, needsPayment} = this.state;
		return (
			<Fragment>
				<Navbar active="home" items={1}/>
				{this.state.detail && (<JobDetail hide={this.onHide} job={this.state.job} dashboard refresh={this.onRefresh}/>)}
				{!this.state.loaded && (<Loading />)}
				{this.state.loaded && (<div className="dashboard-container">
					<div className="dashboard-head-title">Welcome, {this.props.user.first}</div>
					<div className="dashboard-box">
						{empty(needsPayment) && !empty(job) && (<div className="dashboard-side">
							<span className="dashboard-title">UPCOMING JOB</span>
							<div className="calendar-block">
								<Calendar month={moment.utc(job.date).format("MMM")} day={moment.utc(job.date).format("DD")} dashboard color="white"/>
								<div className="text-calendar-block">
									<span className="smaller-job-confirmation" style={{color: job.status == "Pending" ? "red" : "green"}}>{job.status}</span>
									<span className="smaller-job-confirmation">{job.type}</span>
									<span className="smaller-job-confirmation">{getTime(job.time)}</span>
								</div>
							</div>
							<button className="job-details-btn" onClick={this.setDetail}>JOB DETAILS</button>
						</div>)}
						{!empty(needsPayment) && (<div className="dashboard-side">
							<span className="dashboard-title">PAYMENT NEEDED</span>
							<div className="calendar-block">
								<Calendar month={moment.utc(needsPayment.date).format("MMM")} day={moment.utc(needsPayment.date).format("DD")} dashboard color="white"/>
								<div className="text-calendar-block">
									<span className="smaller-job-confirmation" style={{color: needsPayment.status == "Pending" ? "red" : "green"}}>{needsPayment.status}</span>
									<span className="smaller-job-confirmation">{needsPayment.type}</span>
									<span className="smaller-job-confirmation">{getTime(needsPayment.time)}</span>
								</div>
							</div>
							<div className="job-details-btn"><a href={needsPayment.invoice_url} style={{color: "white", width: "100%", height: "100%", display: "flex", justifyContent: "center", alignItems: "center"}} target="_blank">PAY HERE</a></div>
						</div>)}
						{this.state.job == null && empty(needsPayment) && (<div className="dashboard-side">
							<img className="book-img" src={bookimg} alt=""/>
							<div className="book-now-container">
								<span className="dashboard-title">Book a Job</span>
								<span className="book-now-desc" style={{fontSize: "0.95rem"}}>A college-aged worker is eager to help you with anything you need! Always ${(WORKER_RATE).toFixed(2)} per man per hour!</span>
								<Link className="book-now-btn" to="/app/dashboard/book">BOOK NOW</Link>
							</div>
						</div>)}
						<div className="dashboard-side cust-dash-pic">
							<img className="book-img" src={joelogo} alt=""/>
						</div>
					</div>
				</div>)}
			</Fragment>
		)
	}
}

const mapStateToProps = state => ({
  user: state.auth.user
});

export default connect(mapStateToProps,{logoutUser})(withRouter(Checkout));
