import {apiCall} from '../../services/api';
import {GET_PRODUCTS, REMOVE_PRODUCT} from '../types';
import empty from 'is-empty';

export const state_get_products = payload => ({
	type: GET_PRODUCTS,
	payload
});

export const state_remove_job = i => ({
	type: REMOVE_PRODUCT,
	i
});

export const db_add_product = product => dispatch => {
	return apiCall('post', `/api/products`, product)
	.then(function(res) {
		return {complete: true};
	})
	.catch(function(err) {
		return {complete: false, message: err};
	});
}
