import React from 'react'
import StripeCheckout from 'react-stripe-checkout';

import CheckoutBtn from '../Graphics/CheckoutBtn';

import icon from '../../images/icon.png';

export default class CheckoutPay extends React.Component {
  onToken = (token) => {
    console.log(token);
  }

  render() {
    return (
      <StripeCheckout
        name="PSK Apparel"
        token={this.onToken}
        stripeKey="pk_test_jzVfc9MR4WDKN9FVwaLZzj5N"
        allowRememberMe={false}
        currency="USD"
        panelLabel={`$${(this.props.amount / 100).toFixed(2)}`}
        ComponentClass={<CheckoutBtn />}
        image={icon}
      />
    )
  }
}
