import React, {Component, Fragment} from 'react';
import {Link, withRouter} from 'react-router-dom';
import {connect} from "react-redux";
import {registerUser, loginUser} from "../../store/actions/workerauth";
import {db_forgot_password} from "../../store/actions/workers";
import {setFooter} from "../../store/actions/footer";
import empty from 'is-empty';
import {states} from '../../utils/options';
import checkTrue from '../../utils/checkTrue';
import validator from 'validator';

import signinPic from '../../images/moving.jpeg';

import Navbar from '../../components/Nav/Navbar';
import ErrorBanner from '../../components/Graphics/ErrorBanner';
import AddressBlock from '../../components/Forms/AddressBlock';
import Loading from '../../components/Graphics/Loading';
import ImagePicker from '../../components/Graphics/ImagePicker';

import defaultPic from '../../images/defaultPic.png';
import defaultID from '../../images/defaultID.png';
import defaultVideo from '../../images/defaultVideo.png';
import fullVideo from '../../images/fullVideo.png';

class WorkerSignIn extends Component {
	constructor(props) {
		super(props);
		this.state = {
			data: {
				first: "",
	      last: "",
	      email: "",
	      phone: "",
	      address: "",
	      city: "",
	      state: "",
	      zip: "",
	      profile: "",
	      password: "",
	      password2: "",
	      video: "",
	      idpic: ""
			},
			validation: {
				phone: true,
				email: true,
				state: true
			},
			empties: [],
      error: "",
      signin: this.props.signin,
      forgot: false,
      forgotconfirm: false,
      loaded: true,
      src: null,
      registerSuccess: false
		};
	}

	componentWillReceiveProps(nextProps) {
		if (nextProps.auth.isAuthenticated) {
			if (nextProps.auth.user.db == "customers") {
				this.props.setFooter("customers");
				this.props.history.push("/app/dashboard");
			} else if (nextProps.auth.user.db == "workers") {
				this.props.setFooter("workers");
				this.props.history.push("/worker/dashboard");
			} else {
				this.props.setFooter("admin");
				this.props.history.push("/admin/dashboard");
			}
    }
    if(nextProps.signin != this.props.signin) this.setState({...this.state, signin: nextProps.signin});
  }

  componentDidMount() {
    // If logged in and user navigates to Login page, should redirect them to dashboard
    if (this.props.auth.isAuthenticated) {
    	if (this.props.auth.user.db == "customers") {
				this.props.setFooter("customers");
				this.props.history.push("/app/dashboard");
			} else if (this.props.auth.user.db == "workers") {
				this.props.setFooter("workers");
				this.props.history.push("/worker/dashboard");
			} else {
				this.props.setFooter("admin");
				this.props.history.push("/admin/dashboard");
			}
    } else {
    	this.props.setFooter("signin");
    }
  }

  imgHandler = e => {
  	e.preventDefault();
  	if(e.target.files.length > 0) this.imgChange(e, this.setImgState);
  }

  videoHandler = e => {
  	e.preventDefault();
  	if(e.target.files.length > 0) this.imgChange(e, this.setVideoState);
  }

  IDHandler = e => {
  	e.preventDefault();
  	if(e.target.files.length > 0) this.imgChange(e, this.setIDState);
  }

  imgChange = (e, cb) => {
		var reader = new FileReader();
    reader.onload = function() { cb(reader.result) };
    reader.readAsDataURL(e.target.files[0]);
	}

	setImgState = blob => {
		this.setState({
    	...this.state,
    	src: blob
    });
	}

	setVideoState = blob => {
		this.setState({
    	...this.state,
    	data: {
    		...this.state.data,
    		video: blob
    	}
    });
	}

	setIDState = blob => {
		this.setState({
    	...this.state,
    	data: {
    		...this.state.data,
    		idpic: blob
    	}
    });
	}

	setImg = blob => {
		this.setState({
			...this.state,
			data: {
				...this.state.data,
				profile: blob
			},
			src: null
		});
	}

  onChange = e => {
    this.setState({
    	...this.state,
    	data: {
    		...this.state.data,
    		[e.target.id]: e.target.value 
    	} 
    });
  };

  onPhoneChange = e => {
  	const ok = validator.isMobilePhone(e.target.value, 'en-US');
		this.setState({
			...this.state,
			data: {
				...this.state.data,
				phone: e.target.value
			},
			validation: {
				...this.state.validation,
				phone: ok
			}
		});
  }

  onEmailChange = e => {
  	const ok = validator.isEmail(e.target.value);
		this.setState({
			...this.state,
			data: {
				...this.state.data,
				email: e.target.value
			},
			validation: {
				...this.state.validation,
				email: ok
			}
		});
  }

  onStateChange = e => {
  	const ok = states.includes(e.target.value.toUpperCase());
		this.setState({
			...this.state,
			data: {
				...this.state.data,
				state: e.target.value
			},
			validation: {
				...this.state.validation,
				state: ok
			}
		});
  }

  onLogin = async e => {
    e.preventDefault();
    this.setState({
    	...this.state,
    	loaded: false
    });
		const userData = {
      email: this.state.data.email,
      password: this.state.data.password
    };
		const resp = await this.props.loginUser(userData, this.props.history); // since we handle the redirect within our component, we don't need to pass in this.props.history as a parameter
		if(!resp.complete) {
			this.setState({
				...this.state,
				error: resp.message,
				loaded: true
			});
		}
  };

  checkEmpties = () => {
  	let arr = [];
  	let data = this.state.data;
  	for (var key in data) {
	    if (empty(data[key])) {
	      arr.push(key);
	    }
		}
		if(empty(arr)) {
			return true;
		} else {
			this.setState({
				...this.state,
				empties: arr
			});
			return false;
		}
  }

  onRegister = async e => {
    e.preventDefault();
    await this.setState({
  		...this.state,
  		loaded: false,
  		empties: []
  	});
  	const empties = await this.checkEmpties();
  	if(empties && checkTrue(this.state.validation)) {
  		const resp = await this.props.registerUser(this.state.data, this.props.history);
  		if(!resp.complete) this.setState({...this.state, error: resp.message, loaded: true});
  			else this.setState({...this.state, loaded: true, registerSuccess: true});
  	}
  };

  resetPassword = async e => {
  	e.preventDefault();
  	this.setState({...this.state, loaded: false});
  	const resp = await this.props.db_forgot_password(this.state.data);
  	resp.complete ? this.setState({...this.state, loaded: true, forgotconfirm: true, forgot: false}) : this.setState({...this.state, loaded: true, error: resp.message});
  }

	render() {
		const {data, validation, empties, registerSuccess} = this.state;
		return (
			<Fragment>
				<Navbar signin worker/>
				<div className="absolute-background-div worker"></div>
				{!empty(this.state.error) && (<ErrorBanner error={this.state.error} onHide={() => this.setState({...this.state, error: ''})}/>)}
				{!this.state.loaded && (<Loading />)}
				{!empty(this.state.src) && (<ImagePicker cancel={() => {this.setState({...this.state, src: null})}} setImg={this.setImg} src={this.state.src}/>)}
				{this.state.loaded && (<div className="sign-in-container worker">
					{!registerSuccess && this.state.signin && (
					<div className="sign-in-box-pic worker">
						<form className="sign-in-box worker" onSubmit={this.onLogin}>
							<span className="sign-in-title">Worker Sign In</span>
							<div className="input-group">
								<span className="field-label">Email</span>
								<input type="text" className="text-field worker" id="email" onChange={this.onChange}/>
							</div>
							<div className="input-group">
								<span className="field-label">Password</span>
								<input type="password" className="text-field worker" id="password" onChange={this.onChange}/>
							</div>
							<span className="helper" style={{textAlign: "start", margin: "0"}}><a className="helper-link" onClick={() => {this.setState({...this.state, signin: false, forgot: true})}}>Forgot Password?</a></span>
							<button type="submit" className="sign-in-btn">Sign In</button>
							<div className="or-div">
								<hr style={{width: "40%", borderBottom: "1px solid grey"}}/>
								<span>or</span>
								<hr style={{width: "40%", borderBottom: "1px solid grey"}}/>
							</div>
							<span style={{textAlign: "center", fontSize: "1.2rem"}}>Don't have an account?</span>
							<Link className="sign-in-btn link" to="/worker/signup">Get Started Here</Link>
						</form>
						<img className="signin-pic" src={signinPic} alt=""/>
					</div>)}
					{!registerSuccess && this.state.forgot && (<form className="sign-in-box worker" style={{height: "16rem", maxWidth: "25rem"}} onSubmit={this.resetPassword}>
						<span className="sign-in-title">Reset Password</span>
						<div className="input-group">
							<span className="field-label">Email</span>
							<input type="text" className="text-field worker" id="email" onChange={this.onChange} value={data.email}/>
						</div>
						<button className="sign-in-btn" type="submit" disabled={empty(data.email)}>Send Reset Email</button>
						<span className="helper"><a className="helper-link" onClick={() => {this.setState({...this.state, forgot: false, signin: true})}}>Back to Sign In.</a></span>
					</form>)}
					{!registerSuccess && this.state.forgotconfirm && (<form className="sign-in-box worker" style={{height: "14rem", maxWidth: "25rem"}} onSubmit={this.resetPassword}>
						<span className="sign-in-title">Email Sent</span>
						<span style={{textAlign: "center"}}>Check your email for your temporary password which you will use to sign in!</span>
						<span className="helper"><a className="helper-link" onClick={() => {this.setState({...this.state, forgot: false, forgotconfirm: false, signin: true})}}>Back to Sign In.</a></span>
					</form>)}
					{!registerSuccess && !this.state.signin && !this.state.forgot && !this.state.forgotconfirm && (<div className="sign-up-box worker" style={{width: "32rem"}}>
						<span className="sign-in-title">Worker Application</span>
						<form className="input-side-container" onSubmit={this.onRegister}>
							<div className="input-side">
								<div className="input-group">
									<span className="field-label">First Name</span>
									<input type="text" className="text-field worker" id="first" onChange={this.onChange}/>
									{empties.includes("first") && (<span className="form-error">You must enter a first name.</span>)}
								</div>
								<div className="input-group">
									<span className="field-label">Last Name</span>
									<input type="text" className="text-field worker" id="last" onChange={this.onChange}/>
									{empties.includes("last") && (<span className="form-error">You must enter a last name.</span>)}
								</div>
								<div className="input-group">
									<span className="field-label">Email</span>
									{!validation.email && (<span className="form-error">Enter a valid email.</span>)}
									<input type="text" className="text-field worker" id="email" onChange={this.onChange}/>
									{empties.includes("email") && (<span className="form-error">You must enter an email.</span>)}
								</div>
								<div className="input-group">
									<span className="field-label">Phone</span>
									{!validation.phone && (<span className="form-error">Enter a valid phone number.</span>)}
									<input type="text" className="text-field worker" id="phone" onChange={this.onChange}/>
									{empties.includes("phone") && (<span className="form-error">You must enter a phone number.</span>)}
								</div>
							</div>
							<div className="input-side">
								<AddressBlock onChange={this.onChange} onStateChange={this.onStateChange} state={this.state} worker/>
							</div>
							<div className="picture-group">
								<img className="profile-pic" src={empty(data.profile) ? defaultPic : data.profile} alt=""/>
								<div className="input-group bottom-form">
									<span className="field-label">Headshot:</span>
									<input className="file-input" type="file" accept="image/*" name="IMAGE" onChange={this.imgHandler}/>
									{empties.includes("profile") && (<span className="form-error">You must enter a profile picture.</span>)}
								</div>
							</div>
							<div className="picture-group">
								<img className="profile-pic" src={empty(data.idpic) ? defaultID : data.idpic} alt="" style={{width: "auto"}}/>
								<div className="input-group bottom-form">
									<span className="field-label">School ID Photo:</span>
									<input className="file-input" type="file" accept="image/*" name="IMAGE" onChange={this.IDHandler}/>
									{empties.includes("idpic") && (<span className="form-error">You must enter a school ID picture.</span>)}
								</div>
							</div>
							<div className="picture-group">
								<img className="profile-pic" src={empty(data.video) ? defaultVideo : fullVideo} alt=""/>
								<div className="input-group bottom-form">
									<span className="field-label">Application Video (30 seconds):</span>
									<input className="file-input" type="file" accept="video/*" name="IMAGE" onChange={this.videoHandler}/>
									{empties.includes("video") && (<span className="form-error">You must enter an application video.</span>)}
								</div>
							</div>
							<div className="input-group bottom-form">
								<span className="field-label">Record a brief 30-second video about why you want to work for Joe's Odd Jobs. Touch on your abilities as a worker and any accomplishments that you are proud of. Make sure to include your face in the video so that we can confirm your identity.</span>
							</div>
							<div className="input-group bottom-form">
								<span className="field-label">Password</span>
								<input type="password" className="text-field worker" id="password" onChange={this.onChange}/>
							</div>
							<div className="input-group bottom-form">
								<span className="field-label">Confirm Password</span>
								<input type="password" className="text-field worker" id="password2" onChange={this.onChange}/>
							</div>
							{this.state.password != this.state.password2 && (<span style={{color: "red", marginLeft: "1%", marginRight: "1%"}}>Passwords don't match</span>)}
							<button type="submit" className="sign-in-btn bottom-form" disabled={this.state.password != this.state.password2}>Apply</button>
							<span className="helper bottom-form" style={{width: "100%"}}>Click <Link className="helper-link" to="/worker/signin">here</Link> to Sign In.</span>
						</form>
					</div>)}
					{registerSuccess && (<div className="sign-in-container no-card">
						<div className="sign-up-box" style={{width: "50rem", borderRadius: "0px", marginTop: "0"}}>
							<span className="sign-in-title">Application Received!</span>
							<div style={{textAlign: "left", fontSize: "1rem"}}>
								<span style={{fontSize: "1.1rem", textAlign: "center"}}>We will contact you by email after reviewing your application. Thank you for your interest in Joe's Odd Jobs!</span>
							</div>
							<div className="book-now-btn" onClick={() => this.setState({...this.state, signin: true, registerSuccess: false})} style={{marginTop: "1rem", cursor: "pointer"}}>BACK TO SIGN IN</div>
						</div>
					</div>)}
				</div>)}
			</Fragment>
		)
	}
}

const mapStateToProps = state => ({
  auth: state.auth,
  errors: state.errors
});

export default connect(mapStateToProps,{registerUser, loginUser, setFooter, db_forgot_password})(withRouter(WorkerSignIn));
