import React, {Component} from 'react';

class GratuityChoice extends Component {

  render() {
  	return(
  		<button className="gratuity-choice-button" onClick={() => this.props.onClick(this.props.amount)} style={this.props.selected ? {background: "#3f6bb4", color: "white"} : {}}>
  			{this.props.amount == 0 ? "NO THANKS" : `$${(this.props.amount).toFixed(2)}`}
	    </button>
  	);
  }
}

export default GratuityChoice;
