import React, {Component, Fragment} from 'react';
import {connect} from "react-redux";
import {Link, withRouter} from 'react-router-dom';

import logo from '../../images/joelogo.png';
import facebook from '../../images/facebook.png';
import linkedin from '../../images/linkedin.png';
import instagram from '../../images/instagram.png';

import orglogo from '../../images/orglogo.png';
import orgfacebook from '../../images/orgfacebook.png';
import orglinkedin from '../../images/orglinkedin.png';
import orginstagram from '../../images/orginstagram.png';

class Footer extends Component {
	constructor(props) {
		super(props);
		this.state = {

		}
	}

	render() {
		return (
			<footer>
	      {this.props.footer == "customers" && (<div className="sub-footer">
	        <div className="new-menu">
	          <div className="new-sub-menu">
	            <Link className="new-menu-item" to="/app/dashboard">Home</Link>
	            <Link className="new-menu-item" to="/app/dashboard/manage">Manage Jobs</Link>
	          </div>
	          <div className="new-sub-menu">
	            <a className="new-menu-item" href="/index.html"><img style={{width: "4rem"}} src={logo} alt=""/></a>
	          </div>
	          <div className="new-sub-menu">
	            <Link className="new-menu-item" to="/app/dashboard/book">Book Job</Link>
	            <Link className="new-menu-item" to="/app/dashboard/account">Account</Link>
	          </div>
	        </div>
	        <hr style={{width: "50%", marginTop: "0.5rem", marginBottom: "0.5rem", borderTop: "1px solid black"}}/>
	        <div className="new-sub-menu">
	          <a className="new-menu-item" href="https://www.instagram.com/joesoddjobs/" target="_blank"><img style={{width: "2rem"}} src={instagram} alt=""/></a>
	          <a className="new-menu-item" href="https://www.linkedin.com/company/joesoddjobs" target="_blank"><img style={{width: "2rem"}} src={linkedin} alt=""/></a>
	          <a className="new-menu-item" href="https://www.facebook.com/Joes-Odd-Jobs-732321266893356/" target="_blank"><img style={{width: "2.15rem"}} src={facebook} alt=""/></a>
	        </div>
	        <span style={{fontSize: "0.7rem"}}>&copy; Joe's Odd Jobs 2019</span>
	      </div>)}
	      {this.props.footer == "workers" && (<div className="sub-footer">
	        <div className="new-menu">
	          <div className="new-sub-menu">
	            <Link className="new-menu-item" to="/app/worker/dashboard">Home</Link>
	            <Link className="new-menu-item" to="/app/worker/dashboard/manage">Manage Jobs</Link>
	          </div>
	          <div className="new-sub-menu">
	            <a className="new-menu-item" href="/index.html"><img style={{width: "4rem"}} src={logo} alt=""/></a>
	          </div>
	          <div className="new-sub-menu">
	            <Link className="new-menu-item" to="/app/worker/dashboard/book">Find Job</Link>
	            <Link className="new-menu-item" to="/app/worker/dashboard/account">Account</Link>
	          </div>
	        </div>
	        <hr style={{width: "50%", marginTop: "0.5rem", marginBottom: "0.5rem", borderTop: "1px solid black"}}/>
	        <div className="new-sub-menu">
	          <a className="new-menu-item" href="https://www.instagram.com/joesoddjobs/" target="_blank"><img style={{width: "2rem"}} src={instagram} alt=""/></a>
	          <a className="new-menu-item" href="https://www.linkedin.com/company/joesoddjobs" target="_blank"><img style={{width: "2rem"}} src={linkedin} alt=""/></a>
	          <a className="new-menu-item" href="https://www.facebook.com/Joes-Odd-Jobs-732321266893356/" target="_blank"><img style={{width: "2.15rem"}} src={facebook} alt=""/></a>
	        </div>
	        <span style={{fontSize: "0.7rem"}}>&copy; Joe's Odd Jobs 2019</span>
	      </div>)}
	      {this.props.footer == "admin" && (<div className="sub-footer">
	        <div className="new-menu">
	          <div className="new-sub-menu">
	            <a className="new-menu-item" href="/index.html"><img style={{width: "4rem"}} src={orglogo} alt=""/></a>
	          </div>
	        </div>
	        <hr style={{width: "50%", marginTop: "0.5rem", marginBottom: "0.5rem", borderTop: "1px solid black"}}/>
	        <div className="new-sub-menu">
	          <a className="new-menu-item" href="https://www.instagram.com/joesoddjobs/" target="_blank"><img style={{width: "2rem"}} src={orginstagram} alt=""/></a>
	          <a className="new-menu-item" href="https://www.linkedin.com/company/joesoddjobs" target="_blank"><img style={{width: "2rem"}} src={orglinkedin} alt=""/></a>
	          <a className="new-menu-item" href="https://www.facebook.com/Joes-Odd-Jobs-732321266893356/" target="_blank"><img style={{width: "2.15rem"}} src={orgfacebook} alt=""/></a>
	        </div>
	        <span style={{fontSize: "0.7rem"}}>&copy; Joe's Odd Jobs 2019</span>
	      </div>)}
	      {this.props.footer == "signin" && (<div className="sub-footer">
	        <div className="new-menu">
	          <div className="new-sub-menu">
	            <a className="new-menu-item" href="/index.html"><img style={{width: "4rem"}} src={logo} alt=""/></a>
	          </div>
	        </div>
	        <hr style={{width: "50%", marginTop: "0.5rem", marginBottom: "0.5rem", borderTop: "1px solid black"}}/>
	        <div className="new-sub-menu">
	          <a className="new-menu-item" href="https://www.instagram.com/joesoddjobs/" target="_blank"><img style={{width: "2rem"}} src={instagram} alt=""/></a>
	          <a className="new-menu-item" href="https://www.linkedin.com/company/joesoddjobs" target="_blank"><img style={{width: "2rem"}} src={linkedin} alt=""/></a>
	          <a className="new-menu-item" href="https://www.facebook.com/Joes-Odd-Jobs-732321266893356/" target="_blank"><img style={{width: "2.15rem"}} src={facebook} alt=""/></a>
	        </div>
	        <span style={{fontSize: "0.7rem"}}>&copy; Joe's Odd Jobs 2019</span>
	      </div>)}
	    </footer>
		)
	}
}

const mapStateToProps = state => ({
  footer: state.footer
});

export default connect(mapStateToProps,{})(withRouter(Footer));
