import React, {Component, Fragment} from 'react';
import {Link, withRouter} from 'react-router-dom';
import {connect} from "react-redux";
import sumItems from '../../utils/sumItems';
import moment from 'moment';
import empty from 'is-empty';

import Navbar from '../../components/Nav/Navbar';
import Loading from '../../components/Graphics/Loading';
import CheckoutPay from '../../components/Forms/CheckoutPay';
import ProductRow from '../../components/Forms/ProductRow';

class Checkout extends Component {
	constructor(props) {
		super(props);
		this.state = {
			loaded: false,
      info: {
        name: '',
        phone: '',
        email: '',
        org: ''
      }
		};
	}

  async componentDidMount() {
  	this.setState({
  		loaded: true
  	});
  }

	render() {

    let product_list = this.props.products.map((product,i) => 
      <ProductRow 
        product={product}
        key={i}
      />
    );

		return (
			<Fragment>
				<Navbar active="home" items={sumItems(this.props.products)}/>
        {!this.state.loaded && (<Loading />)}
        {this.state.loaded && (<div className="dashboard-container">
          <div className="dashboard-box" style={{marginTop: "1rem"}}>
            <div className="personal-info">
              <h3>Personal Info</h3>
              <div className="info-side">
                <label>Name:</label>
                <input type="text" id="name" className="apparel-input"/>
                <label>Phone:</label>
                <input type="tel" id="phone" className="apparel-input"/>
              </div>
              <div className="info-side">
                <label>Email:</label>
                <input type="email" id="email" className="apparel-input"/>
                <label>Organization:</label>
                <input type="text" id="org" className="apparel-input"/>
              </div>
            </div>
            <table className="product-list">
              <tbody className="product-table-body">
                <tr className="product-header">
                  <th className="product-head">Product</th>
                  <th className="product-head">Name</th>
                  <th className="product-head" style={{textAlign: "center"}}>Size</th>
                  <th className="product-head" style={{textAlign: "center"}}>Quantity</th>
                  <th className="product-head">Price</th>
                </tr>
                {!empty(this.props.products) && (<Fragment>{product_list}</Fragment>)}
                {empty(this.props.products) && (<tr>
                  <td colSpan="5">There are no products in your cart.</td>
                </tr>)}
              </tbody>
            </table>
            <div className="checkout-box">
              <span>Total: $100.00</span>
              <span>Items: {sumItems(this.props.products)}</span>
              <CheckoutPay amount={10000}/>
            </div>
          </div>
        </div>)}
			</Fragment>
		)
	}
}

const mapStateToProps = state => ({
  products: state.products
});

export default connect(mapStateToProps,{})(withRouter(Checkout));
