import React, {Component, Fragment} from 'react';
import {Link, withRouter} from 'react-router-dom';
import getTime from '../../utils/getTime';
import moment from 'moment';

class FindRow extends Component {
	constructor(props) {
		super(props);
		this.state = {

		};
	}

	setDetail = e => {
		e.preventDefault();
		this.props.onClick(this.props.job);
	}

	render() {
		const {job} = this.props;
		let color = job.distance < 2 ? "green" : job.distance < 10 ? "orange" : "red";
		return (
			<Fragment>
				{!this.props.card && (<tr className="find-row" onClick={this.setDetail}>
					<td>{moment.utc(job.date).format('MMM Do')}</td>
					<td>{getTime(job.time)}</td>
					<td style={{color: color}}><span >{job.distance.toFixed(1)}</span> mi</td>
					<td>{job.type}</td>
					<td style={{textAlign: "center"}}>{job.hours}</td>
					<td style={{textAlign: "center"}}>{job.workers}</td>
					<td>{job.city}</td>
				</tr>)}
				{this.props.card && (<tr className="find-card" onClick={this.setDetail}>
					<td colSpan="7">
						<div className="find-card-content">
							<div className="find-card-content-side">
								<span className="distance-label" style={{color: color}}>{job.distance.toFixed(1)} mi</span>
								<span className="card-label">{moment.utc(job.date).format('MMM Do')}</span>
								<span className="card-label">{getTime(job.time)}</span>
								<span className="card-label">in {job.city}</span>
							</div>
							<div className="find-card-content-side">
								<span className="distance-label right">{job.type}</span>
								<span className="card-label right">Estimated Hours: {job.hours}</span>
								<span className="card-label right">Workers Needed: {job.workers}</span>
							</div>
						</div>
					</td>
				</tr>)}
			</Fragment>
		)
	}
}

const mapStateToProps = state => ({

});

export default withRouter(FindRow);
