import React, {Component, Fragment} from 'react';
import {Link, withRouter} from 'react-router-dom';
import {WORKER_RATE} from '../../utils/constants';
import empty from 'is-empty';

class CostEstimate extends Component {
	constructor(props) {
		super(props);
		this.state = {
			detail: false
		};
	}

	setDetail = e => {
		e.preventDefault();
		this.setState({
			detail: !this.state.detail
		})
	}

	render() {
		const {job} = this.props;
		return (
			<div className="estimate-container">
				<div className="estimate-div" onClick={this.setDetail}>
					<span className="">${(WORKER_RATE * (empty(job.hours) ? 0 : job.hours) * (empty(job.workers) ? 0 : job.workers)).toFixed(2)}</span>
					{!this.state.detail && (<svg className="chevron black" xmlns="http://www.w3.org/2000/svg" width="36" height="24" viewBox="0 0 24 24"><path d="M10 6L8.59 7.41 13.17 12l-4.58 4.59L10 18l6-6z"/><path d="M0 0h24v24H0z" fill="none"/></svg>)}
					{this.state.detail && (<svg className="chevron black" xmlns="http://www.w3.org/2000/svg" width="36" height="24" viewBox="0 0 24 24"><path d="M7.41 8.59L12 13.17l4.59-4.58L18 10l-6 6-6-6 1.41-1.41z"/><path fill="none" d="M0 0h24v24H0V0z"/></svg>)}
				</div>
				{this.state.detail && (<div className="estimate-detail-div">
					<div className="multiplication-div"><span>x</span></div>
					<div className="estimate-detail-items">
						<span className="estimate-detail">${WORKER_RATE.toFixed(2)}/hr Rate</span>
						<span className="estimate-detail">{empty(job.workers) ? 0 : job.workers} Worker(s)</span>
						<span className="estimate-detail">{empty(job.hours) ? 0 : job.hours} Hour(s)</span>
					</div>
				</div>)}
			</div>
		)
	}
}

export default CostEstimate;
