import React, {Component, Fragment} from 'react';

class WorkerSelection extends Component {
	constructor(props) {
		super(props);
		this.state = {
			selected: true
		};
	}

	render() {
		const {assignment} = this.props;
		const {selected} = this.state;
		return (
			<div onClick={() => {this.props.onClick(assignment.assignment_id); this.setState({selected: !selected})}} className="worker-selection" id={assignment.assignment_id} style={selected ? {backgroundColor: 'lightgreen'} : null}>
				{assignment.first} {assignment.last}
			</div>
		)
	}
}

export default WorkerSelection;
