import React, {Component, Fragment} from 'react';
import {Link, withRouter} from 'react-router-dom';
import {connect} from "react-redux";
import {state_get_products} from "../../store/actions/products";
import sumItems from '../../utils/sumItems';
import moment from 'moment';
import empty from 'is-empty';

import Navbar from '../../components/Nav/Navbar';
import Loading from '../../components/Graphics/Loading';
import ProductCard from '../../components/Forms/ProductCard';

import test from '../../images/dadshort.png';
import test2 from '../../images/spottedsweatshirt.png';

class Products extends Component {
	constructor(props) {
		super(props);
		this.state = {
			loaded: false,
			products: this.props.products,
			items: sumItems(this.props.products)
		};
		this.toCart = this.toCart.bind(this);
	}

  async componentDidMount() {
  	this.setState({
  		loaded: true
  	});
  }

  toCart = products => {
  	this.setState({
  		...this.state,
  		products: [...this.state.products, ...products],
  		items: this.state.items + sumItems(products)
  	});
  	localStorage.setItem("products", [...JSON.parse(localStorage.getItem("products")), ...products]);
  }

  toCheckout = () => {
  	this.props.state_get_products(this.state.products);
  }

	render() {
		return (
			<Fragment>
				<Navbar active="home" items={this.state.items} toCheckout={this.toCheckout}/>
				{!this.state.loaded && (<Loading />)}
				{this.state.loaded && (<div className="dashboard-container">
					<div className="dashboard-box">
						<ProductCard photo={test} product="Dad's Weekend" price={25.00} toCart={this.toCart}/>
						<ProductCard photo={test2} product="Spotted Cow" price={42.00} toCart={this.toCart}/>
					</div>
				</div>)}
			</Fragment>
		)
	}
}

const mapStateToProps = state => ({
  products: state.products
});

export default connect(mapStateToProps,{state_get_products})(withRouter(Products));
