import {GET_PRODUCTS, REMOVE_PRODUCT} from '../types';

const products = (state = [], action) => {
	switch(action.type) {
		case GET_PRODUCTS:
			return [...action.payload];
		case REMOVE_PRODUCT:
			return state.filter((pro, index) => {
				return index != i;
			});
		default:
			return state;
	}
}

export default products;
