import React, {Component, Fragment} from 'react';
import {Link, withRouter} from 'react-router-dom';
import {connect} from "react-redux";
import {logoutUser} from "../../store/actions/auth";
import {db_get_recent_job, db_get_all_paid_jobs} from "../../store/actions/jobs";
import getTime from '../../utils/getTime';
import {WORKER_RATE} from '../../utils/constants';
import moment from 'moment';
import empty from 'is-empty';

import Navbar from '../../components/Nav/Navbar';
import Loading from '../../components/Graphics/Loading';
import ProductCard from '../../components/Forms/ProductCard';

import test from '../../images/dadshort.png';
import test2 from '../../images/spottedsweatshirt.png';

class Products extends Component {
	constructor(props) {
		super(props);
		this.state = {
			loaded: false,
			products: [],
			items: 0
		};
		this.toCart = this.toCart.bind(this);
	}

  async componentDidMount() {
  	this.setState({
  		loaded: true
  	});
  }

  toCart = products => {
  	this.setState({
  		...this.state,
  		products: [...this.state.products, ...products]
  	})
  }

	render() {
		return (
			<Fragment>
				<Navbar active="home" items={this.state.items}/>
				{!this.state.loaded && (<Loading />)}
				{this.state.loaded && (<div className="dashboard-container">
					<div className="dashboard-box">
						<ProductCard photo={test} product="Dad's Weekend" price={25.00} toCart={this.toCart}/>
						<ProductCard photo={test2} product="Spotted Cow" price={42.00} toCart={this.toCart}/>
					</div>
				</div>)}
			</Fragment>
		)
	}
}

const mapStateToProps = state => ({
  user: state.auth.user
});

export default connect(mapStateToProps,{logoutUser})(withRouter(Products));
