import React, {Component, Fragment} from 'react';

class ProductCard extends Component {

  constructor(props) {
    super(props);
    this.state = {
    	products: [],
    	sizes: []
    }
    this.hasValue = this.hasValue.bind(this);
  }

  async getProducts() {

  }

	async hasValue(arr, findValue) {
	  await arr.forEach(function(val, i) {
	      if (typeof val === "object") {
	          if(Object.value(val).includes(findValue)) return i;
	      }
	  })
	  return -1;
	}

  onChange = e => {
  	e.preventDefault();
  	this.setState({
  		...this.state,
  		sizes: this.hasValue(this.state.sizes, e.target.id) ? [...this.state.sizes, {...e.target.id, quantity: e.target.value}] : [...this.state.sizes, {size: e.target.id, quantity: e.target.value}]
  	});
  }

  render() {
    return (
      <div className="dashboard-side">
				<img src={this.props.photo} alt="" className="product-photo"/>
				<div className="product-description">
					<span className="product-title">{this.props.product}</span>
					<span>${this.props.price.toFixed(2)}</span>
					<div className="size-input-div">
						<div className="size-input-choice">
							<input type="text" className="size-input" id="S" type="number" pattern="\d*" onChange={this.onChange}/>
							<span>S</span>
						</div>
						<div className="size-input-choice">
							<input type="text" className="size-input" id="M" type="number" pattern="\d*" onChange={this.onChange}/>
							<span>M</span>
						</div>
						<div className="size-input-choice">
							<input type="text" className="size-input" id="L" type="number" pattern="\d*" onChange={this.onChange}/>
							<span>L</span>
						</div>
						<div className="size-input-choice">
							<input type="text" className="size-input" id="XL" type="number" pattern="\d*" onChange={this.onChange}/>
							<span>XL</span>
						</div>
					</div>
					<button className="add-to-cart">Add to Cart</button>
				</div>
			</div>
    );
  }
}

export default ProductCard;
