import {GET_JOBS, REMOVE_JOB, UPDATE_JOB} from '../types';

const jobs = (state = [], action) => {
	switch(action.type) {
		case GET_JOBS:
			return [...action.payload];
		case REMOVE_JOB:
			return state.filter(job => {
				return job.id !== action.payload.id;
			});
		case UPDATE_JOB:
			return [...state.filter(job => {
				return job.id !== action.payload.id;
			}), action.payload];
		default:
			return state;
	}
}

export default jobs;
