import React, {Component, Fragment} from 'react';
import {Link, withRouter} from 'react-router-dom';
import getTime from '../../utils/getTime';
import moment from 'moment';

class Row extends Component {
	constructor(props) {
		super(props);
		this.state = {

		};
	}

	setDetail = e => {
		e.preventDefault();
		this.props.onClick(this.props.job);
	}

	render() {
		const {job} = this.props;
		return (
			<Fragment>
				<div className="job-row" onClick={this.setDetail}>
					<div className="left-row row-item">{moment.utc(job.date).format('MMM Do')}</div>
					<div className="row-item">{getTime(job.time)}</div>
					<div className="row-item">{job.type}</div>
					<div className="right-row row-item">
						<span className="right-width">{job.address}</span>
						<svg className="chevron" xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24"><path d="M10 6L8.59 7.41 13.17 12l-4.58 4.59L10 18l6-6z"/><path d="M0 0h24v24H0z" fill="none"/></svg>
					</div>
				</div>
				<hr className="row-divider"/>
			</Fragment>
		)
	}
}

